const testCities = ['Ho Chi Minh', 'tokyo', 'Bern, CH'];
cy.visit(_URL.default.serpPage);
cy.wait('@dataLoaded');
cy.wrap(testCities).each(city => {
  cy.searchCity(city);
  cy.wait('@citySuggestionLoaded').then(({
    response
  }) => {
    const {
      body: {
        list: [selectedCity]
      }
    } = response;
    cy.wait('@dataLoaded').then(({
      request,
      response
    }) => {
      const {
        body: {
          lat: actualLat,
          lon: actualLon
        }
      } = response;
      expect(request.url).include(`lat=${selectedCity.coord.lat}`);
      expect(request.url).include(`lon=${selectedCity.coord.lon}`);
      expect(actualLat).to.eq(selectedCity.coord.lat);
      expect(actualLon).to.eq(selectedCity.coord.lon);
      cy.url().should('contain', selectedCity.id);
      cy.getCookie(_Cookie.default.cityId).should('have.property', 'value', String(selectedCity.id));
    });
  });
});

