});

const errorCodes = [{
  code: 500,
  body: {
    cod: 500,
    message: "Internal server error"
  }
}, {
  code: 422,
  body: {
    code: 422,
    message: "invalid input, please retype your city"
  }
}, {
  code: 401,
  body: {
    cod: 401,
    message: "Invalid API key. Please see http://openweathermap.org/faq#error401 for more info."
  }
}];
cy.visit(_URL.default.serpPage);
cy.wrap(errorCodes).each(({
  code,
  body
}) => {
  cy.intercept({
    pathname: _API.default.dataFind
  }, req => {
    req.continue(res => {
      res.statusCode = code;
      res.body = body;
    });
  }).as(`mockedError${code}`);
  cy.searchCity('Bien Hoa, VN');
  cy.wait(`@mockedError${code}`).then(({
    response
  }) => {
    expect(response.statusCode).to.equal(code);
  });
  switch (code) {
    case 500:
      //Assume a modal dialog is shown when error 500 is returned
      cy.get(_Modal.default.frmErrorModal).should('be.visible');
      break;
    case 422:
      //Assume an error validation message is shown when error 422 is returned
      cy.get(_SERP.default.msgValidationError).should('be.visible').should('contain', body.message);
      break;
    case 401:
      //Assume browser redirect to login page when error 401 is returned
      cy.url().should('contain', 'login');
      break;
  }
});

